<? include 'cgi-bin/dbconnect.php';$query = <<<EOQselect 	City, 	Character_Type, 	Splat2, 	Splat1,	count(character_id) as Num_of_Characters from 	wod_characters where 	is_sanctioned='y' 	and is_npc='n' 	and is_deleted = 'n'	and city = 'San Diego'group by 	city, 	character_type, 	Splat2,	Splat1EOQ;$result = mysql_query($query) or die(mysql_error());while($detail = mysql_fetch_array($result)){  echo "$detail[City] : $detail[Character_Type] : $detail[Splat2] : $detail[Splat1] : $detail[Num_of_Characters]<br>";}?>